INSERT INTO log_finders(id, environment, log_path, regex, splunk_query, splunk_query_key) VALUES ('RESET_PASSWORD_SMS','ALL','/var/log/cima/resetpwd-cimaServer$-server.log','\[PH:SMSPIN:AT\]</ns2:Name><ns2:Value>(.*?)</ns2:Value></ns2:MessageAttribute>.*?<ns2:Address>$PHONE_NUMBER</ns2:Address>', 'search index=cima_ndx cima_env=$ENVIRONMENT cima_domain=$DOMAIN sourcetype=*resetpwd* earliest=$EARLIEST "[PH:SMSPIN:AT]" "$PHONE_NUMBER" | rex field=_raw "(?<timestamp>\d{4}\-\d{1,2}\-\d{1,2}.*?\d{1,2}\:\d{1,2}\:\d{1,2}).*\[PH:SMSPIN:AT\]</ns2:Name><ns2:Value>(?<resetcode>\d+)</ns2:Value></ns2:MessageAttribute>" | table timestamp resetcode | sort -timestamp', 'resetcode');
